INSERT INTO t_rules ( `id`, `game_id`, `rule_title`, `rule_date`, `rule_modify`, `rule_content`, `rule_userid` ) VALUES  ('30','','北京师范大学附属中学平谷第一分校迎新杯足球赛','2016-04-12 21:30:19','2016-04-12 21:30:19','<p>高中8人制 ，初中5认制。采取小组淘汰制。</p>','186');
INSERT INTO t_rules ( `id`, `game_id`, `rule_title`, `rule_date`, `rule_modify`, `rule_content`, `rule_userid` ) VALUES  ('31','','阳光体育2016年北京市崇文小学 第二届校内班级联赛5V5足球赛规程','2016-04-13 09:55:14','2016-04-13 09:55:14','<p style=\"text-indent:101px;line-height:32px\"><strong><span style=\"font-size:29px;font-family:黑体\">阳光体育2016年北京市崇文小学</span></strong></p><p style=\"text-indent:101px;line-height:32px\"><strong><span style=\"font-size:29px;font-family:黑体\">第二届校内班级联赛5V5足球赛<a name=\"_GoBack\"></a>规程</span></strong></p><p style=\"line-height:33px\"><strong><span style=\"font-size:19px\">&nbsp;</span></strong></p><p style=\"line-height:33px\"><strong><span style=\"font-size:19px\">&nbsp;</span></strong></p><p style=\"line-height:33px\"><strong><span style=\"font-size:19px;font-family: 宋体\">一、运动资格及参赛办法</span></strong></p><p style=\"text-indent: 38px;line-height: 33px\"><span style=\"font-size:19px\">1. </span><span style=\"font-size:19px;font-family:宋体\">以班级为单位组队报名，每班报名一支队伍参赛，报名人数</span><span style=\"font-size:19px\">10</span><span style=\"font-size:19px;font-family:宋体\">人，其中必须有一名女生参赛。运动员必须是该校的有正式学籍的学生。</span></p><p style=\"text-indent: 38px;line-height: 33px\"><span style=\"font-size:19px;font-family:宋体\">本次比赛设</span></p><p style=\"text-indent: 38px;line-height: 33px\"><span style=\"font-size:19px;font-family:宋体\">小学（六年级组）待定</span></p><p style=\"text-indent: 38px;line-height: 33px\"><span style=\"font-size:19px;font-family:宋体\">小学（五年级组）待定</span></p><p style=\"text-indent: 38px;line-height: 33px\"><span style=\"font-size:19px;font-family:宋体\">小学（四年级组）待定</span></p><p style=\"text-indent: 38px;line-height: 33px\"><span style=\"font-size:19px;font-family:宋体\">小学（三年级组）待定</span></p><p style=\"text-indent:38px;line-height:33px\"><span style=\"font-size:19px\">2. </span><span style=\"font-size:19px;font-family: 宋体\">参赛的运动员必须思想品德好，学习成绩及格，经医务部门检查，身体健康适合该项竞赛活动。加强安全教育，提高安全意识。学生参赛期间的保险由校方负责统一办理。（请学校各班主任填好相应表格）</span></p><p style=\"margin-left:2px;line-height:33px\"><span style=\"font-size:19px\">&nbsp;&nbsp;&nbsp; 3. </span><span style=\"font-size:19px;font-family:宋体\">每队可报领队或教练一人（班主任），运动员</span><span style=\"font-size:19px\">10</span><span style=\"font-size:19px;font-family:宋体\">人。（男女均可）学校主管领导负责本队队员的资格审查，并在报名表上签字，比赛时领队或教练必须到场。</span></p><p style=\"line-height:33px\"><strong><span style=\"font-size:19px;font-family: 宋体\">二、竞赛办法</span></strong></p><p style=\"text-indent:37px;line-height:33px\"><span style=\"font-size:19px\">1. </span><span style=\"font-size:19px;font-family: 宋体\">比赛采用国际足联相关“</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">人制”足球比赛规则。场地</span><span style=\"font-size:19px\">28</span><span style=\"font-size:19px;font-family:宋体\">米</span><span style=\"font-size:19px\">x45</span><span style=\"font-size:19px;font-family:宋体\">米</span> <span style=\"font-size:19px;font-family: 宋体\">，球门高</span><span style=\"font-size:19px\">2</span><span style=\"font-size:19px;font-family:宋体\">米、宽</span><span style=\"font-size:19px\">3</span><span style=\"font-size:19px;font-family:宋体\">米。</span></p><p style=\"text-indent:37px;line-height:33px\"><span style=\"font-size:19px\">2. </span><span style=\"font-size:19px;font-family: 宋体\">比赛采用</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">人制，（可以男女混编），换人次数不限，换下队员可以重新上场，注意先下后上，换人在换人区内完成。</span></p><p style=\"text-indent:37px;line-height:33px\"><span style=\"font-size:19px\">3. </span><span style=\"font-size:19px;font-family: 宋体\">全场比赛</span><span style=\"font-size:19px\">25</span><span style=\"font-size:19px;font-family:宋体\">分钟（毛时），上下半场各</span><span style=\"font-size:19px\">10</span><span style=\"font-size:19px;font-family:宋体\">分钟，中场休息</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">分钟。比赛采用</span><span style=\"font-size:19px\">4</span><span style=\"font-size:19px;font-family:宋体\">号足球。</span></p><p style=\"text-indent:37px;line-height:33px\"><span style=\"font-size:19px\">4. </span><span style=\"font-size:19px;font-family: 宋体\">比赛上场学生一律穿平底或碎丁足球鞋，戴好护腿板。统一颜色服装，号码要清晰固定，队长要带袖标。</span></p><p style=\"text-indent:37px;line-height:33px\"><span style=\"font-size:19px\">5. </span><span style=\"font-size:19px;font-family: 宋体\">各参赛队比赛前</span><span style=\"font-size:19px\">20</span><span style=\"font-size:19px;font-family:宋体\">分钟到场，由教练（班主任）填写本场比赛上场队员名单。</span></p><p style=\"text-indent:28px;line-height:33px\"><span style=\"font-size:19px\">6. </span><span style=\"font-size:19px;font-family: 宋体\">每名队员只能参加一组比赛，不能降组，更不能串组比赛。</span></p><p style=\"text-indent:28px;line-height:33px\"><span style=\"font-size:19px\">7. </span><span style=\"font-size:19px;font-family: 宋体\">每场比赛各队班主任必须到场，并对本班学生及学生家长进行管理，保证每场比赛的顺利进行。</span></p><p style=\"text-indent:28px;line-height:33px\"><span style=\"font-size:19px\">8. </span><span style=\"font-size:19px;font-family: 宋体\">对于违规的参赛队，除进行批评教育外还要视情节和问题性质给与处罚，直至取消比赛资格并全校通报。</span></p><p style=\"line-height:33px\"><strong><span style=\"font-size:19px;font-family: 宋体\">三、录取名次及计分方法</span></strong></p><p style=\"line-height:33px\"><strong><span style=\"font-size:19px;font-family: 宋体\">采用单场淘汰制：</span></strong></p><p style=\"text-indent:28px;line-height:33px\"><span style=\"font-size:19px\">1. </span><span style=\"font-size:19px;font-family: 宋体\">各组别录取前四名</span></p><p style=\"text-indent:28px;line-height:33px\"><span style=\"font-size:19px\">2. </span><span style=\"font-size:19px;font-family: 宋体\">胜者晋级，输者淘汰。无故弃权取消比赛成绩。</span></p><p style=\"text-indent:28px;line-height:33px\"><span style=\"font-size:19px\">3. </span><span style=\"font-size:19px;font-family: 宋体\">如一场比赛直至结束仍保持平局，以点球决胜负。</span></p><p style=\"line-height:40px\"><strong><span style=\"font-size:19px;font-family: 宋体\">四</span></strong><strong><span style=\"font-size:21px;font-family: 宋体\">、报名时间、地点及方式</span></strong></p><p style=\"text-indent:37px;line-height:40px\"><span style=\"font-size:19px\">1.</span><span style=\"font-size:19px;font-family: 宋体\">报名时间：</span><span style=\"font-size:19px\">2016</span><span style=\"font-size:19px;font-family:宋体\">年</span><span style=\"font-size:19px\">3</span><span style=\"font-size:19px;font-family:宋体\">月</span><span style=\"font-size:19px\">28</span><span style=\"font-size:19px;font-family:宋体\">日</span><span style=\"font-size:19px\">—4</span><span style=\"font-size:19px;font-family:宋体\">月</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">日</span></p><p style=\"text-indent:25px;line-height:40px\"><br/></p><table cellpadding=\"0\" cellspacing=\"0\"><tbody><tr class=\"firstRow\"><td width=\"92\" height=\"35\" bgcolor=\"white\" style=\"vertical-align:top;background:   white\"><br/></td></tr></tbody></table><p><span style=\"font-size:19px\">2.</span><span style=\"font-size:19px;font-family:宋体\">报名地点：教学楼二层广播室</span></p><p><br/></p><p style=\"text-indent:37px;line-height:40px\"><span style=\"font-size:19px\">3.</span><span style=\"font-size:19px;font-family: 宋体\">报名方式：以电子邮件及纸质报名表形式，纸质报名表二份须加盖学校公章。</span></p><p style=\"text-indent:37px;line-height:40px\"><span style=\"font-size:19px;font-family:宋体\">联系人：</span><span style=\"font-size:19px\">&nbsp; </span><span style=\"font-size:19px;font-family:宋体\">辛洪亮</span><span style=\"font-size: 19px\">&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:19px;font-family:宋体\">王立峰</span><span style=\"font-size:19px\">&nbsp; </span></p><p style=\"text-indent:37px;line-height:40px\"><span style=\"font-size:19px;font-family:宋体\">联系电话：</span><span style=\"font-size:19px\">13701379559 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;13466502695</span></p><p style=\"line-height:33px\"><strong><span style=\"font-size:21px;font-family: 宋体\">五</span></strong><strong><span style=\"font-size:19px;font-family: 宋体\">、领队会时间、地点：待定</span></strong></p><p style=\"line-height:40px\"><strong><span style=\"font-size:19px;font-family: 宋体\">六、比赛时间：</span></strong><strong><span style=\"font-size:19px\">2016</span></strong><strong><span style=\"font-size:19px;font-family: 宋体\">年</span></strong><strong><span style=\"font-size:19px\">4</span></strong><strong><span style=\"font-size:19px;font-family: 宋体\">月</span></strong><strong><span style=\"font-size:19px\">6</span></strong><strong><span style=\"font-size:19px;font-family: 宋体\">日—</span></strong><strong><span style=\"font-size:19px\">2016</span></strong><strong><span style=\"font-size:19px;font-family: 宋体\">年</span></strong><strong><span style=\"font-size:19px\">6</span></strong><strong><span style=\"font-size:19px;font-family: 宋体\">月</span></strong></p><p style=\"text-indent:37px;line-height:40px\"><strong><span style=\"font-size:19px;font-family:宋体\">地</span></strong><strong><span style=\"font-size:19px\">&nbsp;&nbsp;&nbsp; </span></strong><strong><span style=\"font-size:19px;font-family:宋体\">点：崇文小学足球场</span></strong></p><p style=\"line-height:40px\"><strong><span style=\"font-size:19px;font-family: 宋体\">本规程其它未尽事宜以主办单位通知为准。</span></strong></p><p style=\"line-height:33px\"><span style=\"font-size:19px;color:red\">&nbsp;</span></p><p style=\"line-height:33px\"><span style=\"font-size:19px;color:red\">&nbsp;</span></p><p style=\"text-align:center;line-height:32px\"><strong><span style=\"font-size:16px;font-family:黑体\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></strong><strong><span style=\"font-size:19px;font-family:黑体\">&nbsp;&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:29px;font-family:宋体\">五人制足球竞赛规则说明</span></strong></p><p style=\"line-height: 39px\"><span style=\"font-size:19px\">&nbsp;</span><span style=\"font-size:19px;font-family:宋体\">一、比赛场地：</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">（一）比赛场长</span><span style=\"font-size: 19px\">45</span><span style=\"font-size:19px;font-family:宋体\">米，宽</span><span style=\"font-size:19px\">28</span><span style=\"font-size:19px;font-family: 宋体\">米。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">（二）中圈：以球场中点为圆心，以</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family: 宋体\">米为半径划一圆圈为中圈。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">（三）中线：场地的中间线为中线。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">（四）罚球区：球门线中点为圆心，以</span><span style=\"font-size:19px\">3</span><span style=\"font-size:19px;font-family: 宋体\">米为半径划一半圆与端线相连构成罚球区。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">（五）换人区：在替补席一侧边线与中线交接点左右两米，各划</span><span style=\"font-size:19px\">30</span><span style=\"font-size:19px;font-family:宋体\">厘米线垂直于边线。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">（六）线宽：各线宽不得超过</span><span style=\"font-size:19px\">12</span><span style=\"font-size:19px;font-family: 宋体\">厘米</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">（七）球门：高为</span><span style=\"font-size:19px\">2</span><span style=\"font-size:19px;font-family: 宋体\">米，宽为</span><span style=\"font-size:19px\">3</span><span style=\"font-size:19px;font-family:宋体\">米。</span></p><p style=\"text-indent: 19px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">二、比赛用四号球。</span></p><p style=\"text-indent: 19px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">三、队员：</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">上场比赛</span><span style=\"font-size: 19px\">5</span><span style=\"font-size:19px;font-family:宋体\">人，设守门员，换人不限，比赛可以随时换人，但必须在规定的换人区内先出后进。被红牌取消资格者不得复入，场内不足三人时比赛即停止，并判对方胜。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">四、比赛时间：分上下半场，各</span><span style=\"font-size:19px\">10</span><span style=\"font-size:19px;font-family: 宋体\">分钟，中间休息</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">分钟。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">五、比赛设守门员，守门员可在罚球内用手触球：</span></p><p style=\"line-height: 39px\"><span style=\"font-size:19px\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:19px;font-family:宋体\">罚则：</span><span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">在比赛中进行</span><span style=\"font-size:19px;font-family:&#39;Arial&#39;,&#39;sans-serif&#39;;color:#333333;background:white\">“</span><span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">机动替换</span><span style=\"font-size:19px;font-family:&#39;Arial&#39;,&#39;sans-serif&#39;;color:#333333;background:white\">”</span><span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">，替补队员在被替补队员还未完全离场时就进场，裁判员应立即停止比赛，由主裁判员令被替换队员离场，对入场的替补队员给予警告，并由对方在停止比赛时球所在地点踢间接任意球恢复比赛。如果当时球在罚球区内，则应在停止比赛时距球所在地点最近的</span><span style=\"font-size:19px;font-family:&#39;Arial&#39;,&#39;sans-serif&#39;;color:#333333;background:white\">6</span><span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">米线上执行。</span> <span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">如果替换队员未在替换区内进行，裁判员亦应立即停止比赛，由主裁判员警告有关队员，并由对方在停止比赛时球所在地点踢间接任意球恢复比赛。如果当时球在罚球区内，则应在停止比赛时距球所在地点最近的</span><span style=\"font-size:19px;font-family:&#39;Arial&#39;,&#39;sans-serif&#39;;color:#333333;background:white\">6</span><span style=\"font-size:19px;font-family:宋体;color:#333333;background:white\">米线上执行。</span></p><p style=\"margin-left: 42px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">六、罚点球决胜方法：以对方场上的五名队员轮流踢，五人踢满后如仍平局，则以第一罚球人顺序踢，直至分出胜负。</span></p><p style=\"text-indent: 35px;line-height: 39px\"><br/></p><table cellpadding=\"0\" cellspacing=\"0\"><tbody><tr class=\"firstRow\"><td width=\"92\" height=\"35\" bgcolor=\"white\" style=\"vertical-align:top;background:   white\"><br/></td></tr></tbody></table><p><span style=\"font-size:19px;font-family:宋体\">七、不能铲球和冲撞，违者由对方罚直接任意球。</span></p><p><br/></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">八、离球</span><span style=\"font-size: 19px\">5</span><span style=\"font-size:19px;font-family:宋体\">米与</span><span style=\"font-size:19px\">4</span><span style=\"font-size:19px;font-family: 宋体\">秒违例：</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">任意球、球门球、角球和踢界外球时，守方队员必须自觉退离球</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">米；中圈开球，必须站在中圈外；而罚球队员必须在</span><span style=\"font-size:19px\">4</span><span style=\"font-size:19px;font-family:宋体\">秒钟内踢出，如</span><span style=\"font-size:19px\">4</span><span style=\"font-size:19px;font-family:宋体\">秒钟内不将球踢出，则判由对方罚间接任意球（发球门球违例，则由对方在中线上一点开球；踢任意球、角球、界外球违例，则由对方在原地开球恢复比赛。另：如果遇守方队员在本方犯规又离罚球区不足</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">米距离时，犯规地点退至离守方罚球区</span><span style=\"font-size:19px\">5</span><span style=\"font-size:19px;font-family:宋体\">米处执行）。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">九、球门球、角球：</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">球被攻方队员碰出端线，由守方队员在罚球区内任何一点用脚开球门球，球开出罚球区、比赛方为开始。球被守方碰出端线，则由攻方开角球。角球应把球放定在端线与边线的交点上踢出。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">十、守方队员在本方罚球区内犯规，一律判罚点球，罚球点距球门</span><span style=\"font-size:19px\">7</span><span style=\"font-size:19px;font-family:宋体\">米。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">十一、界外球恢复比赛用脚踢球，踢球时，球必须停放在线上不动，支撑脚不能踩进场内。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">十二、罚球点球：</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">将球放在中点，攻守双方队员（除主罚队员外）必须站在中线后面并距球</span><span style=\"font-size:19px\">3</span><span style=\"font-size:19px;font-family:宋体\">米，罚球队员必须将球向前踢出，如球中途停止，双方队员在无违例情况下可以抢球。</span></p><p style=\"text-indent: 35px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">十三、比赛中可随时换人。换人不限，但必须在换人区进行，先出后进（被出牌罚出场的运动员不得复入）。</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">十四、取消比赛资格：</span></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">在比赛中被裁判员出示红牌或同一场比赛被出示两次黄牌者，当即停止该场比赛资格。</span></p><p style=\"text-indent: 28px;line-height: 39px\"><br/></p><table cellpadding=\"0\" cellspacing=\"0\"><tbody><tr class=\"firstRow\"><td width=\"8\" height=\"9\" bgcolor=\"white\" style=\"vertical-align:top;background:white\"><br/></td></tr></tbody></table><p><span style=\"font-size:19px;font-family:宋体\">队员被红牌罚出场后一分钟，可由同队替补队员补足参赛人数，如在一分钟内被对方攻入一球，无须等到一分钟后方能替补；如两队同时为两人参赛时，如有进球，两队可同时补足人数。</span></p><p><br/></p><p style=\"text-indent: 37px;line-height: 39px\"><span style=\"font-size:19px;font-family:宋体\">十五、被红牌罚令出场的队员（除即时取消该场比赛资格外），下一轮停赛与否，视犯规情节轻重而定，由组委会发出停赛通知，如无特殊情况，无须停赛。</span></p><p><span style=\"font-size:19px;font-family:宋体\">十六、本规则说明的解释权归赛事组委会。</span></p><p><br/></p><table cellpadding=\"0\" cellspacing=\"0\"><tbody><tr class=\"firstRow\"><td width=\"262\" height=\"667\"><br/></td><td width=\"262\" height=\"667\"><br/></td></tr><tr><td><br/></td><td width=\"97\" height=\"35\" bgcolor=\"white\" style=\"vertical-align:top;background:   white\"><br/></td></tr></tbody></table><p><span style=\"font-size:21px\">&nbsp;</span></p><p><br/></p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p><br clear=\"ALL\"/> </p><p style=\"margin-left:179px;line-height:32px\"><stron