INSERT INTO t_rules ( `id`, `game_id`, `rule_title`, `rule_date`, `rule_modify`, `rule_content`, `rule_userid` ) VALUES  ('32','96','北京市大兴区第三小学第一届梦想足球赛','2016-04-13 11:55:13','2016-04-13 11:55:13','<p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:48px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:48px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">北京市大兴区第三小学</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">第一届梦想足球赛</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">活动指导思想</span></strong></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:仿宋\">校园足球活动的开展是以增强学生体质，培养青少年拼搏进取、团结协作的体育精神为宗旨，在少年儿童中普及足球知识和技能，形成校园足球文化，从而培养全面发展、特长突出的足球后备人才，是发展中国足球的特色之路。</span></p><p style=\"text-indent:37px\"><span style=\"font-size:19px;font-family:仿宋\">足球年级联赛是校园足球活动的核心，是足球活动发展的载体，是检验训练成效、促进项目健康发展的积极手段，随着大兴区足球联赛的持续推进和良性运行，我校不仅取得了一些成绩，并且在校园中已经涌现出了一大批具有足球运动特长的学生，足球运动空前高涨，足球联赛势在必行。</span></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">嘉 宾 成 员</span></strong></p><p><span style=\"font-size: 19px;font-family:宋体\">韩宝刚&nbsp; 李克明 吴清林&nbsp; 郭连鹏&nbsp; 袁伟&nbsp; 韩起峋北京女足队员</span></p><p><span style=\"font-size: 19px;font-family:宋体\">德茂协作区各校体育组长&nbsp; </span></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">主&nbsp; 席&nbsp; 团</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体\">主&nbsp; 席：</span></strong><span style=\"font-size: 19px;font-family:宋体\">陈宗禹 </span></p><p><strong><span style=\"font-size:19px;font-family:宋体\">主席团成员：</span></strong><span style=\"font-size:19px;font-family:宋体\">陈秋霞 于海涛 李丽霞 范福海 郭颖杰 刘洪月 黄建</span></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:24px;font-family:宋体\">仲裁委员会</span></strong></p><p style=\"margin-left: 83px\"><strong><span style=\"font-size:19px;font-family:宋体\">仲&nbsp;&nbsp; 裁：</span></strong><span style=\"font-size: 19px;font-family:宋体\">陈宗禹 陈秋霞 曹金存 贾万丰 霍磊</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:24px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:24px;font-family:宋体\">竞&nbsp; 赛&nbsp; 办</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体\">主&nbsp; 任：</span></strong><span style=\"font-size: 19px;font-family:宋体\">霍磊</span></p><p><strong><span style=\"font-size:19px;font-family:宋体\">竞赛组：</span></strong><span style=\"font-size:19px;font-family:宋体\">曹金存 杨洪祥 贾万丰 马宁 霍磊 田甜</span></p><p><strong><span style=\"font-size:19px;font-family:宋体\">宣 传 组：</span></strong><span style=\"font-size:19px;font-family:宋体\">于海涛 崔书良 李春燕 李魁 王志起 王玥 </span></p><p><strong><span style=\"font-size:19px;font-family:宋体\">安 保 组：</span></strong><span style=\"font-size:19px;font-family:宋体\">黄建 张志文</span></p><p><strong><span style=\"font-size:19px;font-family:宋体\">医务 组：</span></strong><span style=\"font-size:19px;font-family:宋体\">崔洁</span></p><p><strong><span style=\"font-size:19px;font-family:宋体\">比赛监督：</span></strong><span style=\"font-size:19px;font-family:宋体\">陈宗禹 陈秋霞于海涛 李丽霞 范福海 郭颖杰 刘洪月 </span></p><p><span style=\"font-size: 19px;font-family:宋体\">黄建</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:24px;font-family:宋体\">裁判委员会</span></strong></p><p><strong><span style=\"font-size:19px;font-family:宋体\">总裁判长：</span></strong><span style=\"font-size:19px;font-family:宋体\">霍磊</span></p><p><strong><span style=\"font-size:19px;font-family:宋体\">裁判员：</span></strong><span style=\"font-size:19px;font-family:宋体\">曹金存 杨洪祥 贾万丰 马宁 霍磊 田甜</span></p><p><span style=\"font-size:19px;font-family:宋体\">&nbsp;</span></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:24px;font-family:宋体\">北京市大兴区第三小学第一届梦想杯足球赛</span></strong></p><p style=\"text-indent:221px\"><strong><span style=\"font-size:29px;font-family: 宋体\">规&nbsp;&nbsp;&nbsp;&nbsp; 程</span></strong></p><p style=\"margin-left: 48px;line-height: 200%\"><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">一、<span style=\"font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span></strong><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">主办单位：</span></strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">大兴区第三小学</span></p><p style=\"margin-left: 48px;line-height: 200%\"><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">二、<span style=\"font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span></strong><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">比赛时间：</span></strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">2016</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">年4月15日下午13:15</span></p><p style=\"margin-left: 48px;line-height: 200%\"><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">三、<span style=\"font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span></strong><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">比赛地点：</span></strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">大兴区第三小学操场</span></p><p style=\"margin-left: 48px;line-height: 200%\"><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">四、<span style=\"font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span></strong><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">参赛年级：</span></strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\"> 1--6年级学生</span></p><p style=\"margin-left: 48px;line-height: 200%\"><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">五、<span style=\"font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span></strong><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">竞赛分组：</span></strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">以年级进行分组，同年级对抗</span></p><p style=\"margin-left: 48px;line-height: 200%\"><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">六、<span style=\"font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span></strong><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">运动员资格：</span></strong></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">1</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">参赛运动员必须为本校本班学生；</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">2</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">参赛运动员必须具有良好思想品德、经医务部门体检合格，身体健康适合该项竞赛活动，并在学校投保人身意外伤害保险者。（<strong>没有参加学校保险的运动员不得报名、参赛。</strong>）学校及各班对运动员保险工作和身体健康情况进行审核落实，加强安全教育，提高安全意识（一旦发生伤害事故，由校方与保险公司解决）。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">3</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">各班主任要对本班运动员的资格负责，并在报名表上签字。比赛时班主任、副班主任必须到场，认真组织队员参赛，加强对本班学生的管理。</span></p><p style=\"line-height: 200%\"><strong><span style=\"font-size:19px;line-height: 200%;font-family:宋体\">七、参赛办法：</span></strong></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">1</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">各代表队以班级为单位进行报名。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">2</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">每队报领队1名、教练1名（由正、副班主任协商）。</span></p><p style=\"line-height:37px\"><span style=\"font-size:19px;font-family:宋体\">运动员：</span><span style=\"font-size: 21px;font-family: 宋体\">报名截至时间4月15号之前</span><span style=\"font-size: 21px;font-family: 仿宋_GB2312\">，</span><span style=\"font-size:19px;font-family:宋体\">名单上交后不得更改。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">&nbsp;</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">3</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">每场比赛前，正、副班主任认真检查参赛运动员本人的身体情况、保险信息，准确无误方可上场比赛。</span></p><p style=\"line-height: 200%\"><strong><span style=\"font-size:19px;line-height: 200%;font-family:宋体\">八、竞赛办法：</span></strong></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">1</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">比赛采用中国足协最新审定的《国际足联FIFA竞赛规则》。严格执行《全国足球比赛纪律处罚条例》和《北京市足球运动协会比赛违规违纪处罚办法》。比赛累计两张黄牌停赛一场，一张红牌停赛一场。比赛中要坚决遏制罢赛、打架、不服从裁判的现象，领队、教练必须以身作则并管理好队伍和本班观众。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">2</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">比赛采用淘汰制。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">3</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">比赛采用4号球，比赛用球由竞赛组提供。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">4</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span> <span style=\"font-size: 20px;line-height: 200%;font-family: 宋体\">校内比赛采取1-4年级4人制限报7人、5-6年级采取5人限报9人赛制的足球班级赛；小学1-4年级每节5分钟，休息2分钟，分三节；5-6年级每半场10分钟中场休息3分钟，分上下半场</span><span style=\"font-size:20px;line-height: 200%;font-family:宋体\">，名单上交后不得更改。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">5</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.1-4</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">年级替补3人，5-6年级</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">每场比赛替补4人 ，一经替换下场，不得重新上场。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">6</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">比赛装备：各队比赛队员必须穿长筒球袜，戴护腿板。<strong>（比赛号服、护腿板由竞赛组提供。）</strong>比赛一律穿布面胶底球鞋，可有胶钉（不许穿由铁钉足球鞋、皮制足球鞋）。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">7</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">场上队长必须<strong>佩带袖标</strong>。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">8</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">淘汰赛每场比赛，胜利的一队晋级下一轮，五个班级的有一只球队抽签轮空直接进入下一轮。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">9</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">如遇两队踢平，互罚点球取胜。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">10</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">杜绝罢赛、打架、群殴等不良现象。领队、教练要以身作则并管理好队员和本班观众，领队具有不可推卸的管理责任。对运动员严格管理、严格要求，要随时做好思想教育工作，发现苗头不对及时妥善解决。一旦出现对裁判员、观众有不礼貌行为；场内、外有打架或群殴行为，则取消该队员的参赛资格并取消班级的优秀组织奖，取消教练优秀教练员评选资格，情节严重者，取消全队比赛成绩，并进行全校大会批评。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">11</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">运动员在比赛前15分钟到达比赛场地，教练填写上场队员名单，认真组织运动员做准备活动。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">12</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">比赛迟到<strong>五分钟</strong>为弃权，另一队直接晋级。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">13</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">各队出现以上各种违规违纪现象，进行全校通报批评。</span></p><p style=\"line-height: 200%\"><strong><span style=\"font-size:19px;line-height: 200%;font-family:宋体\">九、录取名次及奖励办法：</span></strong></p><p style=\"text-indent: 37px;line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">各年级组第一名发奖。</span></p><p style=\"margin-left: 48px;line-height: 200%\"><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">十、<span style=\"font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span></strong><strong><span style=\"font-size:19px;line-height:200%;font-family:宋体\">报名办法</span></strong></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">1.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">赛前不按时交参赛名单的按自动弃权算。</span></p><p style=\"line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">2.</span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">报名地点：大兴三小体育教研室</span></p><p style=\"line-height: 200%\"><strong><span style=\"font-size:19px;line-height: 200%;font-family:宋体\">十一、本规程未尽事宜另行通知</span></strong></p><p style=\"line-height:200%\"><strong><span style=\"font-size:19px;line-height:200%;font-family: 宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:200%\"><strong><span style=\"font-size:24px;line-height:200%;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:200%\"><strong><span style=\"font-size:24px;line-height:200%;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:200%\"><strong><span style=\"font-size:24px;line-height:200%;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:200%\"><strong><span style=\"font-size:24px;line-height:200%;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:200%\"><strong><span style=\"font-size:24px;line-height:200%;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:200%\"><strong><span style=\"font-size:24px;line-height: 200%;font-family:宋体\">参赛注意事项</span></strong></p><p style=\"margin-left: 48px;line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">一、<span style=\"font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">各参赛队须认真按照规程要求，加强师生安全教育、制定参加体育比赛安全工作预案、确保参赛学生符合相关保险要求。</span></p><p style=\"margin-left: 48px;line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">二、<span style=\"font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">各参赛队领队全面负责教练、队员、观众的教育与管理。</span></p><p style=\"margin-left: 48px;line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">三、<span style=\"font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">各参赛队必须服从场地工作人员的管理、遵守各项规章制度，自觉爱护比赛场地的各种公共设施。</span></p><p style=\"margin-left: 48px;line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">四、<span style=\"font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">比赛场地禁止使用明火，严禁吸烟、携带含糖饮料进入；严禁吃口香糖和零食。</span></p><p style=\"margin-left: 48px;line-height: 200%\"><span style=\"font-size:19px;line-height:200%;font-family:宋体\">五、<span style=\"font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-size:19px;line-height:200%;font-family:宋体\">各参赛队须自觉遵守比赛时间，确保提前十五分钟到达比赛场地。</span></p><p style=\"text-indent:161px;line-height:200%\"><span style=\"font-size:29px;line-height:200%;font-family:宋体\">&nbsp;</span></p><p style=\"text-indent: 37px;line-height: 200%\"><span style=\"font-size: 19px;line-height:200%;font-family:宋体\">&nbsp;</span></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">北京市大兴区第三小学</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">第一届梦想足球赛</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">分组表</span></strong></p><p><strong><span style=\"font-size:24px;font-family:宋体\">一年组</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px;font-family:宋体\">二年级</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px;font-family:宋体\">三年级</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px;font-family:宋体\">四年级</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px;font-family:宋体\">五年级</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px;font-family:宋体\">六年级</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p><strong><span style=\"font-size:24px\">&nbsp;</span></strong></p><p style=\"line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">北京市大兴区第三小学</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">第一届梦想足球赛</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">成绩表</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"text-align:center;line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">&nbsp;</span></strong></p><p style=\"line-height:33px\"><strong><span style=\"font-size:32px;font-family:宋体\">&nbsp;</span></strong></p><table cellspacing=\"0\" cellpadding=\"0\"><tbody><tr style=\";height:55px\" class=\"firstRow\"><td width=\"139\" valign=\"top\" style=\"border-color: windowtext; border-width: 1px; padding: 0px 7px;\" height=\"55\"><p style=\"text-align:center\"><strong><span style=\"font-size:24px;font-family:   宋体\">班级</span></strong></p></td><td width=\"429\" valign=\"top\" style=\"border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-style: none; padding: 0px 7px;\" height=\"55\"><p style=\"text-align:center\"><strong><span style=\"font-size:24px;font-family:   宋体\">优胜班级</span></strong></p></td></tr><tr><td width=\"139\" valign=\"top\" style=\"border-right-color: windowtext; border-bottom-color: windowtext; border-left-color: windowtext; border-right-width: 1px; border-bottom-width: 1px; border-left-width: 1px; border-top-style: none; padding: 0px 7px;\"><p style=\"text-align:center\"><strong><span style=\"font-size:24px;font-family:   宋体\">一年级</span></strong></p></td><td width=\"429\" valign=\"top\" style=\"border-top-style: none; border-left-style: none; border-bottom-color: windowtext; border-bottom-width: 1px; border-right-color: windowtext; border-right-width: 1px; padding: 0px 7px;\"><br/></td></tr><tr><td width=\"139\" valign=\"top\" style=\"border-right-color: windowtext; border-bottom-color: windowtext; border-left-color: windowtext; border-right-width: 1px; border-bottom-width: 1px; border-left-width: 1px; border-top-style: none; padding: 0px 7px;\"><p style=\"text